TK.LO = TK.LO || {}local SpawnedEnts = {}function TK.LO:CanSpawn(ply, itm)endfunction TK.LO:SpawnItem(ply, id, trace)    local item = TK.IL:GetItem(id)    if !item then return end        local ent = ents.Create(item.class)    ent:SetModel(item.model)    ent:SetPos(trace.HitPos)    ent:SetAngles(trace.HitNormal:Angle() + Angle(90,0,0))    ent:Spawn()    ent:SetPos(trace.HitPos + trace.HitNormal * ((ent:OBBMaxs().z - ent:OBBMins().z) / 2 - ent:OBBCenter().z))        return entendhook.Add("TKDBPlayerData", "TKLO", function(ply, dbtable, idx, data)    if dbtable != "player_loadout" then return end    end)